    public async Task<IEnumerable<Patient>> GetAllPatients()
    {
        await _context.Patients.LoadAsync();
        return _context.Patients.ToList();
    }


    public async Task<IEnumerable<PersonalData>> GetAllPersonalData()
    {
        await _context.PersonalData.LoadAsync();
        return _context.PersonalData.ToList();
    }

    public async Task<IEnumerable<CurrentNeoplasm>> GetCurrentNeoplasms(int patientId)
    {
        var neoplasms = _context.CurrentNeoplasms.Where(e => e.PatientId == patientId);
        return neoplasms.ToList();
    }

    public async Task<PreviousDiagnosticData?> GetPreviousDiagnosticData(int patientId)
    {
        var data = await _context.PreviousDiagnosticData.FindAsync(patientId);
        return data;
    }
